import React from "react";

function Post({ post, onLike }) {
  return (
    <div
      style={{
        border: "1px solid var(--onyx)",
        margin: "10px",
        padding: "10px",
        backgroundColor: "var(--bone)",
        color: "var(--raisin-black)",
      }}
    >
      <h2 style={{ color: "var(--onyx)" }}>{post.title}</h2>
      <p>Likes: {post.likes}</p>
      <button
        onClick={onLike}
        style={{
          backgroundColor: "var(--onyx)",
          color: "var(--isabelline)",
          border: "none",
          padding: "5px 10px",
          cursor: "pointer",
          borderRadius: "4px",
        }}
      >
        Like
      </button>
    </div>
  );
}

export default Post;
